import React, { useState } from 'react'
import Game from './Components/Game';
import Header from './Components/Header'
import { language } from './utils/utils';

function App() {

  const [score, setScore] = useState(0);

  return (
    <div className='w-full h-screen grid gap-6 bg-slate-800 p-2 justify-center'>

      <Header score={score} />
      <Game score={score} />


      <button className='w-24 bg-bone p-3 border-2 border-slate-500 absolute r-4 b-4'>
        {language.includes("tr") ? "Kurallar" : "Rules"}
      </button>
    </div>
  )
}

export default App
