import React, { useState } from 'react'
import Game from './Components/Game';
import Header from './Components/Header'
import Rules from './Components/Modal/Rules';
import { language } from './utils/utils';

function App() {

  const [score, setScore] = useState(0);

  return (
    <div className='w-full h-screen grid gap-2 bg-slate-800 p-8 justify-center'>

      <Header score={score} />
      <Game score={score} />


      <button data-modal-target="staticModal" data-modal-toggle="staticModal" className='w-24 bg-bone p-3 text-slate-500 rounded-lg border-2 border-slate-500 absolute right-4 bottom-4'>
        {language.includes("tr") ? "Kurallar" : "Rules"}
      </button>
      <Rules id="staticModal" data-modal-backdrop="static" tabindex="-1"  />
    </div>
  )
}

export default App
